package com.Library.common;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.config.Customizer;import org.springframework.security.config.annotation.method.configuration.EnableMethodSecurity;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.web.SecurityFilterChain;import org.springframework.web.servlet.handler.HandlerMappingIntrospector;@EnableMethodSecurity(securedEnabled=true, prePostEnabled=true)@EnableWebSecurity@Configurationpublic class GlobalSecurity {  @Bean  SecurityFilterChain setSecuerity(HttpSecurity http, HandlerMappingIntrospector introspector) throws Exception {    http            .csrf((csrf) -> csrf.disable())            .authorizeHttpRequests((authorize) -> authorize                    .anyRequest().authenticated()            )            .httpBasic(Customizer.withDefaults())            .formLogin(Customizer.withDefaults());    return http.build();  }}