package com.Library.models;import jakarta.persistence.*;import lombok.*;import org.springframework.data.annotation.CreatedDate;import java.util.Set;@Entity@Table(name = "book")@NoArgsConstructor@AllArgsConstructor@Getter@Setterpublic class Book {  @Id  @GeneratedValue(strategy = GenerationType.IDENTITY)  private Long id;  @Column(nullable = false)  private String title;  @Column(nullable = false)  private String author;  @Column(nullable = false)  private int publicationYear;  @Column(nullable = false)  private String isbn;  @CreatedDate  private String createdDate;  @OneToMany(mappedBy = "book")  private Set<BorrowingRecord> borrowingRecords;  @Override  public String toString() {    return "Book{" +            "id=" + id +            ", title='" + title + '\'' +            ", author='" + author + '\'' +            ", publicationYear=" + publicationYear +            ", isbn='" + isbn + '\'' +            ", createdDate='" + createdDate + '\'' +            '}';  }}