package com.Library.models;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.*;import lombok.*;import org.springframework.data.annotation.CreatedDate;import java.util.Date;@Entity@Table(name="borrowingRecord")@NoArgsConstructor@AllArgsConstructor@Getter@Setter@ToStringpublic class BorrowingRecord {  @Id  @GeneratedValue(strategy = GenerationType.IDENTITY)  private Long id;  @ManyToOne  @JoinColumn(name = "book_id")  private Book book;  @ManyToOne  @JoinColumn(name = "patron_id")  private Patron patron;  @CreatedDate  private String createdDate;  @Temporal(TemporalType.DATE)  private Date borrowingDate;  @Temporal(TemporalType.DATE)  private Date returnDate;}